import { Component, HostListener } from "@angular/core";

@Component({
  selector: "app-container",
  templateUrl: "./container.component.html",
  styleUrls: ["./container.component.scss"],
})
export class ContainerComponent {
  sideBarOpen = true;
  desktopViewWidth: number = 1100;
  drawerMode: "side" | "over" = "side";
  @HostListener("window:resize", ["$event.target.innerWidth"])
  onResize(width: number) {
    if (width < this.desktopViewWidth) {
      this.drawerMode = "over";
      if (this.sideBarOpen) {
        this.sideBarOpen = false;
      }
    } else {
      this.drawerMode = "side";
      if (!this.sideBarOpen) {
        this.sideBarOpen = true;
      }
    }
  }

  constructor() {}

  ngOnInit(): void {
    console.log("Hello world!");
  }

  sideBarToggler() {
    this.sideBarOpen = !this.sideBarOpen;
  }

  drawerOpenChangeHandler = (opened: boolean) => {
    this.sideBarOpen = opened;
  };

  onClickMenu() {
    if (this.drawerMode == "over") {
      this.sideBarOpen = false;
    } else {
      this.sideBarOpen = true;
    }
  }
}
<mat-drawer-container>
  <mat-drawer
    [mode]="drawerMode"
    [opened]="sideBarOpen"
    (openedChange)="drawerOpenChangeHandler($event)"
  >
    <div class="logo-wrapper">My Application</div>
    <mat-nav-list>
      <a
        mat-list-item
        routerLink="/home"
        routerLinkActive="list-item-active"
        (click)="onClickMenu()"
      >
        <mat-icon mat-list-icon>home</mat-icon>
        Home
      </a>
      <mat-expansion-panel [expanded]="false">
        <mat-expansion-panel-header>
          <mat-panel-title style="color: #fff">
            <mat-icon>settings</mat-icon>
            <span class="panel-title">Setting</span>
          </mat-panel-title>
        </mat-expansion-panel-header>
        <a
          mat-list-item
          class="expanel-item"
          routerLink="/test"
          routerLinkActive="list-item-active"
          (click)="onClickMenu()"
        >
          <span style="padding-left: 45px">Test</span>
        </a>
      </mat-expansion-panel>
    </mat-nav-list>
  </mat-drawer>
  <mat-drawer-content style="overflow-y: hidden">
    <div class="content nav-margin">
      <!-- [ngStyle]="{
        'margin-left': sideBarOpen && drawerMode == 'side' ? '20px' : ''
      }" -->
      <mat-toolbar style="position: sticky; z-index: 2">
        <mat-toolbar-row class="justify-content-between">
          <button
            class="toggle-btn"
            mat-icon-button
            (click)="sideBarToggler()"
            color="primary"
          >
            <svg
              width="19"
              height="15"
              viewBox="0 0 19 15"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                fill-rule="evenodd"
                clip-rule="evenodd"
