</div> -->
<mat-drawer-container>
  <mat-drawer
    [mode]="drawerMode"
    [opened]="sideBarOpen"
    (openedChange)="drawerOpenChangeHandler($event)"
  >
    <mat-nav-list>
      <a mat-list-item routerLinkActive="list-item-active">HI</a>
    </mat-nav-list>
  </mat-drawer>
  <mat-drawer-content style="overflow-y: hidden">
    <div
      class="content nav-margin"
      [ngStyle]="{
        'margin-left': sideBarOpen && drawerMode == 'side' ? '20px' : ''
      }"
    >
      <mat-toolbar style="position: sticky; z-index: 2">
        <mat-toolbar-row class="justify-content-between"> </mat-toolbar-row>
      </mat-toolbar>
    </div>
    <div class="content content-margin">
      <router-outlet></router-outlet>
    </div>
  </mat-drawer-content>
</mat-drawer-container>
